mapper.insert:

		SELECT LAST_INSERT_ID();


INSERT INTO onclick_refupload (RNAME,RDATE,LIDX,RCONTENTS,FIDX)
VALUES (#{rname}, now(), #{lidx}, #{rcontents}, #{fidx})

