mapper.insert:
INSERT INTO onclick_notice(nidx,nwriter,ncontents,ndate,lidx,sidx,cidx)
SELECT nidx_seq.nextval,#{nwriter},#{ncontents},sysdate,#{lidx},sidx,#{cidx} 
FROM onclick_enroll
WHERE lidx=#{lidx}

INSERT INTO onclick_notice(nidx,nwriter,ncontents,ndate,lidx,sidx,tuidx)
SELECT nidx_seq.nextval,#{nwriter},#{ncontents},sysdate,#{lidx},sidx,#{tuidx} 
FROM onclick_enroll
WHERE lidx=#{lidx}

INSERT INTO onclick_notice(nidx,nwriter,ncontents,ndate,lidx,sidx,lnidx)
SELECT nidx_seq.nextval,#{nwriter},#{ncontents},sysdate,#{lidx},sidx,#{lnidx} 
FROM onclick_enroll
WHERE lidx=#{lidx}

mapper.update:
UPDATE onclick_notice
SET ncheck='Y' 
WHERE nidx=#{0}

UPDATE onclick_notice
SET ndelyn='Y' 
WHERE cidx=#{0}

UPDATE onclick_notice
SET ndelyn='Y' 
WHERE tuidx=#{0}

UPDATE onclick_notice
SET ndelyn='Y' 
WHERE lnidx=#{0}
