mapper.update:
UPDATE onclick_refupload
SET RNAME=#{rname}, RCONTENTS=#{rcontents}
WHERE ridx=#{ridx}

UPDATE onclick_refupload
SET RNAME=#{rname}, RCONTENTS=#{rcontents}, FIDX=#{fidx}
WHERE ridx=#{ridx}

UPDATE onclick_refupload
SET FIDX=null
WHERE ridx=#{ridx}

UPDATE onclick_refupload
SET rdelyn='Y'
WHERE ridx=#{ridx}
