mapper.insert:

		SELECT LAST_INSERT_ID();

INSERT INTO onclick_taskupload (TUNAME,TUSTART,TUFIN,TUDATE,LIDX,TUCONTENTS,FIDX)
VALUES (#{tusubject},#{tustart},#{tufin},now(),#{lidx},#{tucontents},#{fidx})

mapper.update:
