mapper.update:
UPDATE onclick_videoattendence
SET vfull=#{vfull},
	vstart=#{vstart},
	vend=#{vend},
	vpercent=vpercent+#{vpercent},
	vlast=sysdate,
	vattendence=#{vattendence}
WHERE sidx=#{sidx} 
AND cidx=#{cidx}

mapper.select:
SELECT * 
FROM onclick_videoattendence
WHERE sidx=#{sidx} 
AND cidx=#{cidx}
